function berechne(){let e=document.getElementById("drehzahl").value,t=document.getElementById("getriebe").value,n=document.getElementById("ritzel_m").value,d=document.getElementById("ritzel_r").value,l=document.getElementById("radumfang").value,m=e*t*(n/d),u=Math.floor(m*l/1e5*60);!isNaN(u)&&u>0&&(document.getElementById("ausgabe").innerHTML="Geschwindigkeit bei diesen Eingaben: "+u+" KM/H")}